mapper.select:
SELECT id, user_id, organization_code, account_number, current_balance
FROM deposit_accounts
WHERE user_id = #{userId}

mapper.update:
UPDATE deposit_accounts
SET current_balance = #{balance}
WHERE id = #{accountId}

mapper.select:
SELECT COUNT(*) > 0
FROM deposit_accounts
WHERE user_id = #{userId}
  AND account_number = #{accountNumber}
